'use strict'

const test = require('tape')
const parseInfo = require('../src/parse-info.js')

const expected = `"
" scheme s v2.1.0
" description d
" url u
" author: author a
" license: MIT
" background: dark
"
" This file was generated by vim-styler
" https://github.com/jacoborus/vim-styler\n\n`

test('parseInfo: with watermark', t => {
  let data = {
    author: 'author a',
    scheme: 'scheme s',
    background: 'dark',
    description: 'description d',
    license: 'MIT',
    url: 'url u',
    version: '2.1.0'
  }
  t.is(parseInfo(data), expected)
  t.end()
})
